INSERT INTO `tin_tuc` (`id`, `tieu_de`, `mo_ta_tom_tat`, `mo_ta_chi_tiet`, `ngay_tao`, `so_lan_xem`, `trang_thai`, `hinh`, `ma_quan_tri`, `quan_trong`, `search`, `thoi_gian`) VALUES
(11, 'TRƯỜNG ĐẠI HỌC SÀI GÒN TỔ CHỨC GẶP MẶT ĐẦU NĂM MỚI  XUÂN QUÝ TỴ - 2013', 'TRƯỜNG ĐẠI HỌC SÀI GÒN TỔ CHỨC GẶP MẶT ĐẦU NĂM MỚI\r\n\r\nXUÂN QUÝ TỴ - 2013', '<p>\r\n	&nbsp;</p>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: justify;">\r\n	<span style="font-size: 16px;"><span style="font-family: Arial;">Trong kh&ocirc;ng kh&iacute; ấm &aacute;p của những ng&agrave;y đầu xu&acirc;n Qu&yacute; Tỵ 2013, s&aacute;ng ng&agrave;y 19/2/2013 (tức ng&agrave;y mồng 10 th&aacute;ng gi&ecirc;ng &acirc;m lịch), Trường Đại học S&agrave;i G&ograve;n long trọng tổ chức buổi gặp mặt đầu năm cho c&aacute;n bộ cốt c&aacute;n trong trường.&nbsp;</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: justify;">\r\n	<span style="font-family: Arial; font-size: 16px;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span><span style="font-size: 16px;"><span style="font-family: Arial;">Tham dự buổi gặp mặt c&oacute;: PGS.TS. Nguyễn Viết Ngoạn, B&iacute; thư Đảng ủy, Hiệu trưởng Trường Đại học S&agrave;i G&ograve;n, c&aacute;c Ph&oacute; Hiệu trưởng, BCH C&ocirc;ng đo&agrave;n trường, Trưởng, ph&oacute; c&aacute;c đơn vị, B&iacute; thư chi bộ, Chủ tịch CĐBP, B&iacute; thư Đo&agrave;n trường v&agrave; c&aacute;c Ph&oacute; Gi&aacute;o sư, Tiến sĩ kh&ocirc;ng giữ chức vụ l&atilde;nh đạo.&nbsp;</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: justify;">\r\n	<span style="font-family: Arial; font-size: 16px;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span><span style="font-size: 16px;"><span style="font-family: Arial;">Ph&aacute;t biểu tại buổi gặp mặt, PGS.TS. Nguyễn Viết Ngoạn, B&iacute; thư Đảng ủy, Hiệu trưởng nh&agrave; trường gửi lời chức mừng năm mới an khang, thịnh vượng tới tất cả c&aacute;n bộ, giảng vi&ecirc;n, c&ocirc;ng nh&acirc;n vi&ecirc;n to&agrave;n trường. Thầy Hiệu trưởng đ&atilde; n&ecirc;u l&ecirc;n những th&agrave;nh t&iacute;ch đạt được của trường trong năm cũ 2012 v&agrave; c&oacute; lời cảm ơn đối với những cố gắng, nỗ lực, đo&agrave;n kết của đội ngũ c&aacute;n bộ, giảng vi&ecirc;n, c&ocirc;ng nh&acirc;n vi&ecirc;n trong thời gian qua.&nbsp;</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: justify;">\r\n	<span style="font-family: Arial; font-size: 16px;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span><span style="font-size: 16px;"><span style="font-family: Arial;">Thầy Hiệu trưởng cũng hi vọng trong năm mới Qu&yacute; Tỵ 2013, với những đổi mới của to&agrave;n ng&agrave;nh gi&aacute;o dục, Trường Đại học S&agrave;i G&ograve;n sẽ c&oacute; những bước ph&aacute;t triển mới. Đội ngũ sư phạm của trường sẽ c&oacute; nhiều nỗ lực hơn nữa trong mọi c&ocirc;ng t&aacute;c nhằm x&acirc;y dựng Trường Đại học S&agrave;i G&ograve;n đi theo lộ tr&igrave;nh từ một trường đại học ứng dụng l&agrave; chủ yếu, chuyển sang trường đại học nghi&ecirc;n cứu ứng dụng v&agrave; tiến tới l&agrave; một trường đại học nghi&ecirc;n cứu.</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: justify;">\r\n	<span style="font-family: Arial; font-size: 16px;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span><span style="font-size: 16px;"><span style="font-family: Arial;">Buổi gặp mặt diễn ra ngắn gọn nhưng đậm đ&agrave; t&igrave;nh th&acirc;n v&agrave; cũng hứa hẹn một năm mới nhiều th&agrave;nh c&ocirc;ng mới của Trường Đại học S&agrave;i G&ograve;n.</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px;">\r\n	<span style="font-size: 16px;"><span style="font-family: Arial;">&nbsp;</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px;">\r\n	<span style="text-align: justify; font-family: Arial; font-size: 16px;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span><em><strong><span style="font-size: 16px;"><span style="font-family: Arial;">Sau đ&acirc;y l&agrave; một số h&igrave;nh ảnh tại buổi gặp mặt:</span></span></strong></em></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px;">\r\n	&nbsp;</div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: center;">\r\n	<img alt="" height="379" src="http://www.sgu.edu.vn/images/stories/22_2_13_gapmatdaunam/image001.jpg" style="border: none;" width="600" /></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: center;">\r\n	<em><span style="font-size: 14px;"><span style="font-family: Arial;">To&agrave;n cảnh buổi gặp mặt đầu năm</span></span></em></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: center;">\r\n	&nbsp;</div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: center;">\r\n	<img alt="" height="434" src="http://www.sgu.edu.vn/images/stories/22_2_13_gapmatdaunam/image002.jpg" style="border: none;" width="600" /></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: center;">\r\n	<em><span style="font-size: 14px;"><span style="font-family: Arial;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;PGS.TS. Nguyễn Viết Ngoạn, B&iacute; thư Đảng ủy, Hiệu trưởng nh&agrave; trường</span></span></em></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: center;">\r\n	<em><span style="font-size: 14px;"><span style="font-family: Arial;">gửi lời chức mừng năm mới tới tất cả CB-GV-CNV trong trường</span></span></em></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: center;">\r\n	&nbsp;</div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: center;">\r\n	<img alt="" height="395" src="http://www.sgu.edu.vn/images/stories/22_2_13_gapmatdaunam/image003.jpg" style="border: none;" width="600" /></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: center;">\r\n	<em><span style="font-size: 14px;"><span style="font-family: Arial;">&nbsp; &nbsp;PGS.TS. Nguyễn Viết Ngoạn ch&uacute;c mừng năm mới tất cả c&aacute;n bộ, giảng vi&ecirc;n&nbsp;</span></span></em></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: center;">\r\n	<em><span style="font-size: 14px;"><span style="font-family: Arial;">tại buổi gặp mặt đầu năm&nbsp;</span></span></em></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: center;">\r\n	&nbsp;</div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: center;">\r\n	<img alt="" src="http://www.sgu.edu.vn/images/stories/22_2_13_gapmatdaunam/image004.jpg" style="border: none;" width="600" /></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: center;">\r\n	<em><span style="font-size: 14px;"><span style="font-family: Arial;">Niềm vui đầu năm mới của c&aacute;c c&aacute;n bộ, giảng vi&ecirc;n trong trường</span></span></em></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: center;">\r\n	&nbsp;</div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: right;">\r\n	<em><strong><span style="font-size: 16px;"><span style="font-family: Arial;">Tin: Nguy&ecirc;n Hạnh</span></span></strong></em></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: right;">\r\n	<em><strong><span style="font-size: 16px;"><span style="font-family: Arial;">Ảnh: Huỳnh Hữu Đức</span></span></strong></em></div>\r\n', '2013-02-25 09:05:17', 23, 1, '1361757917_image001.jpg', 1, 0, 1, '2013-02-25'),
(12, 'Công đoàn trường Đại học Sài Gòn chào mừng kỷ niệm 103 năm quốc tế phụ nữ (8/3) và 1973 năm cuộc khởi nghĩa hai Bà Trưng', 'CÔNG ĐOÀN TRƯỜNG ĐẠI HỌC SÀI GÒN\r\nCHÀO MỪNG KỶ NIỆM 103 NĂM QUỐC TẾ PHỤ NỮ (8/3)\r\nVÀ 1973 NĂM CUỘC KHỞI NGHĨA HAI BÀ TRƯNG', '<p>\r\n	&nbsp;</p>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px;">\r\n	<span style="font-size: 16px;"><span style="font-family: Arial;">Nh&acirc;n kỷ niệm 103 năm Quốc tế phụ nữ 8/3 v&agrave; 1973 năm cuộc khởi nghĩa Hai B&agrave; Trưng, C&ocirc;ng đo&agrave;n Trường Đại học S&agrave;i G&ograve;n đ&atilde; t&iacute;ch cực tham gia v&agrave; tổ chức c&aacute;c hoạt động ch&agrave;o mừng cấp Th&agrave;nh phố v&agrave; cơ sở như sau:</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px;">\r\n	<span style="font-size: 16px;"><span style="font-family: Arial;">+ Cấp Th&agrave;nh phố: Ng&agrave;y 07/3/2013, Ban Chấp h&agrave;nh C&ocirc;ng đo&agrave;n Trường v&agrave; C&ocirc;ng đo&agrave;n bộ phận (CĐBP) tham gia &nbsp;&ldquo;Ng&agrave;y hội Nữ C&ocirc;ng chức vi&ecirc;n chức &ndash; Lao động&rdquo; tại Cung Văn h&oacute;a Lao động do Li&ecirc;n đo&agrave;n Lao động Th&agrave;nh phố tổ chức, gồm c&aacute;c hoạt động:</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px;">\r\n	<span style="font-size: 16px;"><span style="font-family: Arial;">&bull;<span class="Apple-tab-span" style="white-space: pre;"> </span>Tham gia gian h&agrave;ng &ldquo;Nước Tr&aacute;i c&acirc;y &ndash; Tranh th&ecirc;u&rdquo; phục vụ ng&agrave;y hội;&nbsp;</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px;">\r\n	<span style="font-size: 16px;"><span style="font-family: Arial;">&bull;<span class="Apple-tab-span" style="white-space: pre;"> </span>Tham gia Hội thi cắm hoa vải với chủ đề &ldquo;Thắp s&aacute;ng niềm tin&rdquo; do Thầy Đỗ Ph&uacute;c v&agrave; C&ocirc; L&ecirc; Thị H&ograve;a Dịu &ndash; Khoa Sư phạm Kỹ thuật dự thi với t&aacute;c phẩm &ldquo;Hương sắc 3 miền&rdquo;, kết quả đạt Giải Khuyến kh&iacute;ch;</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px;">\r\n	<span style="font-size: 16px;"><span style="font-family: Arial;">&bull;<span class="Apple-tab-span" style="white-space: pre;"> </span>Tham gia th&agrave;nh phần Ban Gi&aacute;m khảo Hội thi nấu ăn, do TS. Nguyễn Thị Diệu Thảo, Ph&oacute; Trưởng khoa Sư phạm Kỹ thuật đảm tr&aacute;ch;</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px;">\r\n	<span style="font-size: 16px;"><span style="font-family: Arial;">&bull;<span class="Apple-tab-span" style="white-space: pre;"> </span>Ủng hộ 200 c&agrave;nh hoa hồng (trị gi&aacute; 2.000.000 đồng) v&agrave; b&igrave;nh hoa đạt giải trong hội thi (sản phẩm được b&aacute;n đấu gi&aacute; với số tiền 1.700.000 đồng) v&agrave;o Quỹ Học bổng Nguyễn Đức Cảnh của Li&ecirc;n đo&agrave;n Lao động TP.HCM để gi&uacute;p đỡ con của c&aacute;n bộ vi&ecirc;n chức lao động (CBVC-LĐ) Th&agrave;nh phố c&oacute; ho&agrave;n cảnh kh&oacute; khăn, c&oacute; nguy cơ nghỉ học;</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px;">\r\n	<span style="font-size: 16px;"><span style="font-family: Arial;">&bull;<span class="Apple-tab-span" style="white-space: pre;"> </span>Đăng k&yacute; m&ocirc; h&igrave;nh &ldquo;Nu&ocirc;i heo đất&rdquo; g&acirc;y quỹ Học bổng Nguyễn Đức Cảnh tại cơ sở nhằm động vi&ecirc;n khen thưởng con của CBVC-LĐ Trường ĐH S&agrave;i G&ograve;n vượt kh&oacute;, học giỏi;</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px;">\r\n	<span style="font-size: 16px;"><span style="font-family: Arial;">&bull;<span class="Apple-tab-span" style="white-space: pre;"> </span>Tham gia chương tr&igrave;nh &ldquo;Gặp gỡ - trao đổi&rdquo; giữa nữ l&atilde;nh đạo Th&agrave;nh phố với nữ đại biểu CNVC-LĐ (ThS. M&atilde; Thị Kh&aacute;nh T&uacute;, Chủ tịch C&ocirc;ng đo&agrave;n trường dự).</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px;">\r\n	<span style="font-size: 16px;"><span style="font-family: Arial;">+ Cấp cơ sở: C&ocirc;ng đo&agrave;n cơ sở Trường Đại học S&agrave;i G&ograve;n ph&aacute;t động c&aacute;c CĐBP tổ chức nhiều loại h&igrave;nh sinh hoạt mang &yacute; nghĩa thiết thực, ph&ugrave; hợp với đặc th&ugrave; của mỗi đơn vị CĐBP để ch&agrave;o mừng v&agrave; t&ocirc;n vinh vai tr&ograve; người phụ nữ.</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px;">\r\n	<span style="font-size: 16px;"><span style="font-family: Arial;">- Trọng t&acirc;m của hoạt động: &Ocirc;n lại &yacute; nghĩa ng&agrave;y Quốc tế Phụ nữ, cuộc khởi nghĩa Hai B&agrave; Trưng nhằm khơi dậy l&ograve;ng tự h&agrave;o d&acirc;n tộc, truyền thống đấu tranh của phụ nữ Việt Nam, niềm tin v&agrave;o sự l&atilde;nh đạo của Đảng. Tiếp tục qu&aacute;n triệt Nghị quyết số 11-NQ/TW của Bộ Ch&iacute;nh trị về c&ocirc;ng t&aacute;c phụ nữ thời kỳ đẩy mạnh c&ocirc;ng nghiệp h&oacute;a &ndash; hiện đại h&oacute;a đất nước. Tiếp tục ph&aacute;t động phong tr&agrave;o &ldquo;Giỏi việc nước &ndash; Đảm việc nh&agrave;&rdquo;. Chăm lo, tặng qu&agrave; ch&uacute;c mừng nữ C&ocirc;ng chức, vi&ecirc;n chức tại 36 CĐBP.</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px;">\r\n	<span style="font-size: 16px;"><span style="font-family: Arial;">- C&aacute;c hoạt động đa dạng, phong ph&uacute; tại c&aacute;c CĐBP đ&atilde; thu h&uacute;t đ&ocirc;ng đảo c&aacute;n bộ CCVC tham gia như:</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px;">\r\n	<span style="font-size: 16px;"><span style="font-family: Arial;">&bull;<span class="Apple-tab-span" style="white-space: pre;"> </span>Thăm di t&iacute;ch lịch sử: Đền Bến Dược, Địa đạo Củ Chi (CĐBP Ph&ograve;ng Khoa học C&ocirc;ng nghệ); Bảo t&agrave;ng Phụ nữ Nam bộ (CĐBP Văn ph&ograve;ng); Bảo t&agrave;ng Đền thờ Nữ anh h&ugrave;ng V&otilde; Thị S&aacute;u (CĐBP Ph&ograve;ng Đ&agrave;o tạo).&nbsp;</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px;">\r\n	<span style="font-size: 16px;"><span style="font-family: Arial;">&bull;<span class="Apple-tab-span" style="white-space: pre;"> </span>Tổ chức cuộc thi &ldquo;Viết về h&igrave;nh ảnh người Phụ nữ Việt Nam&rdquo; (CĐBP Khoa T&agrave;i ch&iacute;nh Kế to&aacute;n). Tuy&ecirc;n truyền, ph&aacute;t tờ rơi với nội dung &ldquo;Nhật k&yacute; th&aacute;ng 03&rdquo; (CĐBP Khoa Sư phạm kỹ thuật).</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px;">\r\n	<span style="font-size: 16px;"><span style="font-family: Arial;">&bull;<span class="Apple-tab-span" style="white-space: pre;"> </span>Tọa đ&agrave;m chuy&ecirc;n đề &ldquo;Kinh nghiệm nu&ocirc;i con khỏe, dạy con ngoan, vai tr&ograve; người phụ nữ thời hiện đại&rdquo; (CĐBP Bộ m&ocirc;n T&acirc;m l&yacute; Gi&aacute;o dục). Trao đổi kinh nghiệm trong c&ocirc;ng t&aacute;c v&agrave; tổ chức tốt cuộc sống gia đ&igrave;nh&rdquo; (CĐBP Khoa Gi&aacute;o dục Ch&iacute;nh trị). Thảo luận chuy&ecirc;n đề &ldquo;B&igrave;nh đẳng giới t&iacute;nh trong gia đ&igrave;nh&rdquo; (CĐBP Khoa C&ocirc;ng nghệ th&ocirc;ng tin).</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px;">\r\n	<span style="font-size: 16px;"><span style="font-family: Arial;">&bull;<span class="Apple-tab-span" style="white-space: pre;"> </span>Xem v&agrave; b&igrave;nh luận phim tư liệu về cuộc khởi nghĩa Hai B&agrave; Trưng (CĐBP Ph&ograve;ng Thanh tra &ndash; Y tế tra &ndash; TT.ĐTNV&amp;ƯDKTKT &ndash; Ph&ograve;ng Hợp t&aacute;c Quốc tế).</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px;">\r\n	<span style="font-size: 16px;"><span style="font-family: Arial;">&bull;<span class="Apple-tab-span" style="white-space: pre;"> </span>Thi nấu ăn &ldquo;Ph&aacute;i Nam v&agrave;o bếp&rdquo; (CĐBP Khoa Quản trị kinh doanh).</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px;">\r\n	<span style="font-size: 16px;"><span style="font-family: Arial;">&bull;<span class="Apple-tab-span" style="white-space: pre;"> </span>Tổ chức hoạt động vui chơi, d&atilde; ngoại v&agrave; hội thi &ldquo;Gương mặt th&acirc;n quen&rdquo; (CĐBP Khoa Gi&aacute;o dục Mầm non, CĐBP Khoa Gi&aacute;o dục tiểu học, CĐBP Khoa SPKH X&atilde; hội &ndash;Trung t&acirc;m Ph&aacute;t triển gi&aacute;o dục).</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px;">\r\n	&nbsp;</div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: center;">\r\n	<span style="font-size: 16px;"><span style="font-family: Arial;">Sau đ&acirc;y l&agrave; một số h&igrave;nh ảnh c&aacute;c hoạt động:</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: center;">\r\n	&nbsp;</div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: center;">\r\n	<span style="font-size: 16px;"><span style="font-family: Arial;">&nbsp;<img alt="" src="http://www.sgu.edu.vn/images/stories/18032013_CD/image001.jpg" style="border: none;" /></span></span><span style="font-size: 16px;"><span style="font-family: Arial;">&nbsp;</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: center;">\r\n	<span style="font-size: 16px;"><span style="font-family: Arial;">To&agrave;n cảnh gian h&agrave;ng của C&ocirc;ng đo&agrave;n Trường Đại học S&agrave;i G&ograve;n</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: center;">\r\n	<span style="font-size: 16px;"><span style="font-family: Arial;">&nbsp;tham dự hoạt động &ldquo;Ng&agrave;y hội Nữ CCVC-LĐ&rdquo;&nbsp;</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: center;">\r\n	&nbsp;</div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: center;">\r\n	<span style="font-size: 16px;"><span style="font-family: Arial;">&nbsp;</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: center;">\r\n	<img alt="" src="http://www.sgu.edu.vn/images/stories/18032013_CD/image003.jpg" style="border: none; font-family: Arial; font-size: 16px;" /></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: center;">\r\n	<span style="font-size: 16px;"><span style="font-family: Arial;">Thầy: Đỗ Ph&uacute;c v&agrave; C&ocirc;: L&ecirc; Thị H&ograve;a Dịu &ndash; CĐBP Khoa Sư phạm Kỹ thuật</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: center;">\r\n	<span style="font-size: 16px;"><span style="font-family: Arial;">Đạt Giải Khuyến kh&iacute;ch tại Hội thi cắm hoa vải&nbsp;</span></span></div>\r\n<div style="color: rgb(0, 0, 0); font-family: Arial, Helvetica, sans-serif; line-height: 18px; text-align: center;">\r\n	<span style="font-size: 16px;"><span style="font-family: Arial;">với T&a